import React, { useState } from "react";
import "./Header.css";
import Main from "./Main";
import Footer from "./Footer";
import ScrollToTopButton from "./ScrollToTopButton";
import Sidebar from "./Sidebar"; // Sidebar 컴포넌트를 import

const Header = () => {
  const [isSideMenuOpen, setIsSideMenuOpen] = useState(false);

  const toggleSideMenu = () => {
    setIsSideMenuOpen(!isSideMenuOpen);
  };

  const closeSideMenu = () => {
    setIsSideMenuOpen(false);
  };

  return (
    <div className="head">
      <div className="header">
        <h2>로고</h2>
        <div className="menu-icon" onClick={toggleSideMenu}>
          <input
            type="checkbox"
            id="icon"
            checked={isSideMenuOpen}
            onChange={toggleSideMenu}
          />
          <label htmlFor="icon">
            <span></span>
            <span></span>
            <span></span>
          </label>
          {isSideMenuOpen && (
            <Sidebar isOpen={isSideMenuOpen} setIsOpen={closeSideMenu} />
          )}
        </div>
      </div>
      <div className="main">
        <Main />
      </div>
      <div className="foot">
        <Footer />
      </div>
      <div className="allow">
        <ScrollToTopButton />
      </div>
    </div>
  );
};

export default Header;
