import React, { useState } from "react";
import "./Header.css";
import Main from "./Main";
import Footer from "./Footer";
import ScrollToTopButton from "./ScrollToTopButton";

const Header = () => {
  const [isSideMenuOpen, setIsSideMenuOpen] = useState(false);

  const toggleSideMenu = () => {
    setIsSideMenuOpen(!isSideMenuOpen);
  };

  const closeSideMenu = () => {
    setIsSideMenuOpen(false);
  };

  return (
    <div className="head">
      <div className="header">
        <h2>로고</h2>
        <div className="menu-icon" onClick={toggleSideMenu}>
          <input
            type="checkbox"
            id="icon"
            checked={isSideMenuOpen}
            onChange={toggleSideMenu}
          />
          <label htmlFor="icon">
            <span></span>
            <span></span>
            <span></span>
          </label>
          {isSideMenuOpen && (
            <div className="side-menu" onClick={(e) => e.stopPropagation()}>
              {/* X 표시 또는 다른 내용 추가 */}
              <span className="close-icon" onClick={closeSideMenu}>
                &times;
              </span>
              <p>Side Menu Item 1</p>
              <p>Side Menu Item 2</p>
              <p>Side Menu Item 3</p>
            </div>
          )}
        </div>
      </div>
      <div className="main">
        <Main />
      </div>
      <div className="foot">
        <Footer />
      </div>
      <div className="allow">
        <ScrollToTopButton />
      </div>
    </div>
  );
};

export default Header;
