// Header.js
import React, { useState } from "react";
import "./Header.css";
import Main from "./Main";
import Footer from "./Footer";
import ScrollToTopButton from "./ScrollToTopButton";
import Button from "@material-ui/core/Button"; // Button 컴포넌트를 불러옴
import MenuIcon from "./MenuIcon"; // MenuIcon 컴포넌트를 불러옴

const Header = () => {
  const [isOpen, setMenu] = useState(false);

  const toggleMenu = () => {
    setMenu((isOpen) => !isOpen);
  };

  const buttonStyles = {
    // 여기에 필요한 버튼 스타일을 추가하세요
  };

  return (
    <div className="head">
      <div className="header">
        <h2>로고</h2>
        <ul className="header-wrapper">
          <li>
            <MenuIcon onClick={() => toggleMenu()} />
          </li>
          <li>
            <img width="45px" src="#" alt="Logo" />
          </li>
          <li>
            <Button
              variant="contained"
              color="secondary"
              size="small"
              style={buttonStyles}>
              딜리버리주문
            </Button>
          </li>
        </ul>
        <ul className={isOpen ? "show-menu" : "hide-menu"}>
          <li>1</li>
          <li>2</li>
          <li>3</li>
          <li>4</li>
        </ul>
      </div>
      <div className="main">
        <Main />
      </div>
      <div className="foot">
        <Footer />
      </div>
      <div className="allow">
        <ScrollToTopButton />
      </div>
    </div>
  );
};

export default Header;
