// Header.js
import React from "react";
import "./Header.css";
import Main from "./Main";
// import Footer from "./Footer";

const Header = () => {
  return (
    <div className="header">
      <h2>로고</h2>
      <div className="menu-icon">
        <input type="checkbox" id="icon" />
        <label htmlFor="icon">
          <span></span>
          <span></span>
          <span></span>
        </label>
        <div className="dropdown-content">
          <p>Home</p>
          <p>About</p>
          <p>Products</p>
          <p>Contact</p>
        </div>
      </div>
      <Main />
      {/* <Footer /> */}
    </div>
  );
};

export default Header;
